function PopupWithForm({isOpen, name, title, children, btnText}){
    const handleOverlayClick = (event) => {
        if (event.target === event.currentTarget && isOpen) onClose();
    };

    useEffect(() => {
        const handleEscClick = (event) => {
            if (event.key === "Escape") onClose();
        };

        document.addEventListener("keydown", handleEscClick);

        return () => {
            document.removeEventListener("keydown", handleEscClick);
        };
    }, [isOpen, onClose]);

    return(
        <div className={`popup popup_type_${name} ${isOpen ? "popup_opened" : ""}`}>
            <form name="edit" action="#" className={`popup__form popup__form_type_${name}`} noValidate>
                <button type="button" className="popup__close-btn" />
                <h2 className="popup__title">{title}</h2>
                {children}
                <button id="add-submit-btn" type="submit" className="popup__save-btn">{btnText}</button>
            </form>
        </div>
    )
}

export default PopupWithForm
