import React from 'react';
import {CurrentUserContext} from '../contexts/CurrentUserContext'
function Card(props) {
    const userContext = React.useContext(CurrentUserContext)

    function handleClick() {
        props.onCardClick(props.card);
    }

    return (
        <div className="element">
            <img className="element__image" src={userContext.card.link} alt={userContext.card.name} onClick={handleClick}/>
            <button type="button" className="card__del-button"></button>
            <div className="element__box">
                <h2 className='element__name'>{userContext.card.name}</h2>
                <div className="element__like-box">
                    <button type="button" className="element__like"></button>
                    <span className="element__like-counter">{userContext.card.likes.length}</span>
                </div>
            </div>
        </div>
    )
}

export default Card;
